scriptlet.script:
report = scriptletContext.getLocal("report");
warning = scriptletContext.getLocal("warning");
error = scriptletContext.getLocal("error");

if (parseInt(scriptletContext.getLocal("code")) != 0) {
   report += "Unable to get NIC information: " + error + "\n";   
   warning += "Unable to get NIC information: " + error + "\n";
} else {
   data = "";
   nicinfo = scriptletContext.getLocal("nicInfo");
   if (nicinfo != null) {
      data += nicinfo;
      report += nicinfo;

      // check each line and see if we need to add a warning
      lines = data.split(/\n/);
      for (i = 0; i < 100; i++) {
         if (lines[i] == null) break;
         if (lines[i] == "") continue;
         if (lines[i].match("No errors") != null) continue;
         warning += lines[i] + "\n";
      }  // end for
   }  else { //if (nicinfo == null)
      report  += "Unable to get NIC card info\n";
      warning += "Unable to get NIC card info\n";
   }
}

scriptletContext.put("report",report);
scriptletContext.put("warning",warning);

report = scriptletContext.getLocal("report");
warning = scriptletContext.getLocal("warning");
error = scriptletContext.getLocal("error");

if (parseInt(scriptletContext.getLocal("code")) != 0) {
   report += "Unable to get File System information: " + error + "\n";   
   warning += "Unable to get File System information: " + error + "\n";
} else {
   fileList = scriptletContext.getLocal("fileSystemList");
   if (fileList != null) {
      // data is formatted in two columns: % mountname
      // the first row is a header
      // to split data from context it must be turned back into a native Javascript string
      // to do this we initialize a blank string and then append the variable
      fileData = "" + fileList;
      lines = fileData.split(/\s+/);

      for (i = 2; i < 1000; i += 2) {
         if (lines[i] != null && lines[i + 1] != null)   {
            diskreport = "File System " + lines[i + 1] + " is at " + lines[i] + " capacity\n";
            if (parseInt(lines[i]) >= 90) {
               report += "WARNING: ";
               warning += diskreport;
            }
            report += diskreport;
         }
         else {
            break;
         }
      }
   }
}

scriptletContext.put("report",report);
scriptletContext.put("warning",warning);

warning = scriptletContext.getLocal("warning");
report = scriptletContext.getLocal("report");
error = scriptletContext.getLocal("error");

// verify the operation worked
code = parseInt(scriptletContext.getLocal("code"));
if (code != 0) {
   report += "Unable to get uptime information ";
   warning += "Unable to get uptime information ";

   if (error != null)
   {
       warning = warning + ": " + error ;
       report = report + ": " + error;
   }

   report += "\n";
   warning += "\n"; 
}
else  {
   // get the number of minutes
   mins = parseInt(scriptletContext.getLocal("minutes"))
   // turn into a more friendly string
   hrs = mins / 60;
   days = hrs / 24;
   hours = hrs % 24;
   minutes = mins % 60;
   report += "System has been up for ";
   if (days > 0) report += java.lang.Math.floor(days).toString() + " day";
   if (days > 1) report += "s";
   if (hours > 0) report += ", " + java.lang.Math.floor(hours).toString() + " hour";
   if (hours > 1) report += "s";
   report += " and " + minutes.toString() + " minute";
   if (minutes != 1) report += "s";
   report += "\n";
   if (mins <= 15) warning += "System was rebooted in the last 15 minutes\n";
}

scriptletContext.put("report",report);

report = scriptletContext.getLocal("report");
if (report == null) {
    report = "";
}

warning = scriptletContext.getLocal("warning");
if (warning == null) {
    warning = "";
}

error = scriptletContext.getLocal("logError");

if (error != null && error != "" && error != "None") {
   report += "Unable to get log information: " + error + "\n";   
   warning += "Unable to get log information: " + error + "\n";
} else {
   minutes = scriptletContext.getLocal("minutes");
   if (minutes >= 0) {
       report += "Last log message was " + (parseInt(minutes)) + " minutes ago\n";
       if (minutes <= 15) warning += "Log messages in the last 15 minutes\n";
   } else {
       report += "Unable to get log information. Check user permissions.\n";
       warning += "Unable to get log information. Check user permissions.\n";
   }
}

scriptletContext.put("report", report);
scriptletContext.put("warning", warning);

