var internalError = false;

if (parseInt(scriptletContext.getLocal("code")) != 0) {
   report += "Unable to get iostat information: " + error + "\n";   
   warning += "Unable to get iostat information: " + error + "\n";
} 
else {
   iostat = scriptletContext.getLocal("iostatInfo");
   // deal with the iostat information
   // this assume we are getting extended IO stat information and looks for anomolies
   // there should be two readings, the first is the average since boot,
   // the second one is the current 

   output = "\nDevice IO checks:\n";
   if (iostat != null) {
      // first, split it all into lines
      data = "" + iostat;
      lines = data.split(/\n/);

      // find the two lines that start with "Device:"
      first_set = -1;
      second_set = -1;
      for (i = 0; i < 1000; i++) {
         if (lines[i] == null) break;
         if (lines[i].match(/Device:/) != null) {
            if (first_set == -1) {
               first_set = i + 1;
            } else {
               second_set = i + 1;
            }
         }
      }

      // how many lines are there?
      line_count = i - second_set - 1;
      if (line_count > 0) {
         for (i = 0; i < line_count; i++) {

            // toss out any blank lines
            aline = first_set + i;
            cline = second_set + i;
            if ((lines[aline] == "") || (lines[cline]=="") || (lines[aline] == null) || (lines[cline] ==null)) continue;

            // split the average line and current line into columns
            average = lines[aline].split(/\s+/);
            current = lines[cline].split(/\s+/);
            this_output = "";

            try{
            // compare key stats of each lines average versus now
            name = current[0];

            creads = 0;
            if (java.lang.Double.parseDouble(current[3])) {
                creads = java.lang.Double.parseDouble(current[3]);
            }

            areads = 0;
            if (java.lang.Double.parseDouble(average[3])) {
                areads = java.lang.Double.parseDouble(average[3]);
            }

            cwrites = 0;
            if (java.lang.Double.parseDouble(current[4])) {
                cwrites = java.lang.Double.parseDouble(current[4]);
            }

            awrites = 0;
            if (java.lang.Double.parseDouble(average[4])) {
                awrites = java.lang.Double.parseDouble(average[4]);
            }

            cwait = 0;
            if (java.lang.Double.parseDouble(current[9])) {
                cwait = java.lang.Double.parseDouble(current[9]);
            }

            await = 0;
            if (java.lang.Double.parseDouble(average[9])) {
                await = java.lang.Double.parseDouble(average[9]);
            }

            csvctm = 0;
            if (java.lang.Double.parseDouble(current[10])) {
                csvctm = java.lang.Double.parseDouble(current[10]);
            }

            avctm = 0;
            if (java.lang.Double.parseDouble(average[10])) {
                asvctm = java.lang.Double.parseDouble(average[10]);
            }

            cutil = 0;
            if (java.lang.Double.parseDouble(current[11])) {
                cutil = java.lang.Double.parseDouble(current[11]);
            }

            autil = 0;
            if (java.lang.Double.parseDouble(average[11])) {
                autil = java.lang.Double.parseDouble(average[11]);
            }

            // look for some anomolies
            if (cwait > (await * 1.25)) this_output += "Current disk wait on " + name + " is more than 25% above average\n";
            if (cwait > 30 && cutil > 5) this_output += "Current disk wait time exceeds 30 ms on " + name + "\n";
            if (cutil >= 60) this_output += name + " is over 60% busy\n";
            if (creads > (areads * 3)) this_output += "Current reads on " + name + " are more than 300% of average\n";
            if (cwrites > (awrites * 3)) this_output += "Current writes on " + name + " are more than 300% of average\n";

            }catch(e)
            {
            	internalError = true;
            }
            // check to see if we found anything
            if (this_output == "") {
               this_output = name + " looks normal\n";
            } else {
               warning += this_output;
            }
            output += this_output;
         }
      } 

      // add in the current raw data
      output += "\nDetailed iostat information:\n";
      if (internalError == true)
          output += "Unable to get iostat information\n";
      else { 

          for (i = second_set - 1; i < 10000; i++) {
             if (lines[i] == null) break;
             output += lines[i] + "\n";
          }  
      } //end if (internalError == true)
      report += output;
       }

}

scriptletContext.put("report",report);
scriptletContext.put("warning",warning);

report = scriptletContext.getLocal("report");
warning = scriptletContext.getLocal("warning");
error = scriptletContext.getLocal("error");

if (parseInt(scriptletContext.getLocal("code")) != 0) {
   report += "Unable to get process list: " + error + "\n";   
   warning += "Unable to get process list: " + error + "\n";
} 
else {
   // see if the vital processes are running
   // assume vital process list is delimited by comma, pipe, space or newline
   processes = vital.split(/,|\||\s|\n/);
   missing = "false";
   list = scriptletContext.getLocal("list");
   if (processes != null) {
      for (i = 0; i < 10000; i++) {

         if (processes[i] == null) break;

         if (list.match(processes[i]) == null) {
            // we have a missing process
            report += processes[i] + " is missing\n";
            warning += processes[i] + " is missing\n";
            missing = "true";
         }
      }
    }
    if (missing == "false") report += "All vital processes are running\n";
}

scriptletContext.put("report",report);
scriptletContext.put("warning",warning);

report = scriptletContext.getLocal("report");
warning = scriptletContext.getLocal("warning");
error = scriptletContext.getLocal("error");

if (parseInt(scriptletContext.getLocal("code")) != 0) {
   report += "Unable to get Memory Information: " + error + "\n";   
   warning += "Unable to get Memory Information: " + error + "\n";
} 
else {
   report += "\n" + host + " has " + scriptletContext.getLocal("totalMemory") + " MB memory, " + scriptletContext.getLocal("freeMemory") + " MB free\n";
   report += host + " has " + scriptletContext.getLocal("totalSwap") + " MB swap, " + scriptletContext.getLocal("freeSwap") + " MB free\n";
   freeMem = parseInt(scriptletContext.getLocal("freeMemory"));
   totalMem = parseInt(scriptletContext.getLocal("totalMemory"));
   freeSwap = parseInt(scriptletContext.getLocal("freeSwap"));
   totalSwap = parseInt(scriptletContext.getLocal("totalSwap"));
   usedSwap = parseInt(scriptletContext.getLocal("usedSwap"));
   pctMem = (freeMem * 100)/totalMem;
   pctSwap = (freeSwap * 100)/totalSwap;

   // check for bad conditions
   output = "";
   if (pctMem < 15) output += "Less than 15% of memory is free\n";
   if (pctSwap < 15) output += "Swap space is almost exhausted\n";
   if (usedSwap > (.5 * totalMem)) output += "Swap usage exceeds 50% of total memory\n"; 

   // add any anomalies to our report
   report += output;
   warning += output
}

report += "\n";
scriptletContext.put("report",report);
scriptletContext.put("warning",warning);

