if (parseInt(scriptletContext.getLocal("code")) != 0) {
   report += "Unable to get Load Averages: " + error + "\n";   
   warning += "Unable to get Load Averages: " + error + "\n";
} else {
   report += "Load average is " + scriptletContext.getLocal("oneMinute") + " " + scriptletContext.getLocal("fiveMinute") + " " 	+ scriptletContext.getLocal("fifteenMinute") + "\n";

   // how does the load compare to the total number of processors?
   numProcs = parseInt(scriptletContext.getLocal("numProcs"));
   one = scriptletContext.getLocal("oneMinute");
   five = scriptletContext.getLocal("fiveMinute");
   fifteen = scriptletContext.getLocal("fifteenMinute");
   threshold = numProcs * .8;

   if (one > threshold) {
      // has it been high for a while?
      if (five > threshold) {
         report += "System is heavily loaded\n";
         warning += "System is heavily loaded\n";
      } else {
         report += "System appears to have a CPU spike\n";
      }
   } else {
      // is the system underutilized?
      threshold = threshold = numProcs * .2;
      if (one <= threshold) {
         report += "CPU usage is low: System may be under-utilized\n";
         warning += "CPU usage is low: System may be under-utilized\n";
      } else {
         report += "System is operating at normal load\n";
      }
   }
}

scriptletContext.put("report", report);
scriptletContext.put("warning", warning);

