if (code != 0) {
   warning = report = "Unable to ping " + host;
   if (error != null)
   {
       warning = warning + ": " + error ;
       report = report + ": " + error;
   }

   report += "\n";
   warning += "\n";    
}
else {
   // check the max latency
   maxLatency = parseInt(scriptletContext.getLocal("max latency"));
   report += "Maximum ping latency was " + maxLatency.toString() + " ms\n";
   if (maxLatency > 200) warning += "Pinging " + host + " took longer than 200ms\n";

   minLatency = parseInt(scriptletContext.getLocal("min latency"));
   report += "Minimum ping latency was " + minLatency.toString() + " ms\n";

   averageLatency = parseInt(scriptletContext.getLocal("average latency"));
   report += "Average ping latency was " + averageLatency.toString() + " ms\n";

   // check the packet loss
   packetLoss = parseInt(scriptletContext.getLocal("% packet loss"));
   report += "Packet loss was " + packetLoss.toString() + " %\n";
   if (packetLoss > 0) warning += "Ping had " + packetLoss.toString() + "% packet loss\n\n";
}

scriptletContext.put("report",report);
